<?php	// Dewpoint calculation// IN: t in F, h= humidity %// OUT: dewpoint infunction dewpoint($t,$h){	$t= ($t-32)*5/9;	// convert to C	$H= (log10($h)-2)/0.4343 + (17.62*$t)/(243.12+$t);	return round(((243.12*$H)/(17.62-$H))*9/5 +32,1);	// back to F}function olddewpoint($t,$h){    // a crude approximation    return round($t - ((100-$h)/2.778),1);}$t = 57; $h = 17;echo "Dewpoint 1, 2 ",dewpoint($t,$h)," ",olddewpoint($t,$h),"\n";?>